.site:hover {
    background-color: rgba(255, 255, 255, 0.4);
    transition-delay: 0.1s;
}

.site-abbreviation {
    font-family: var(--maint-text-font);
    height: 50px;
    font-size: 50px;
    text-align: center;
    margin: 5px auto 5px auto;
