    .site {
        width: 150px;
    }

    .sites {
        margin-top: 22px;
    }
}

@media (min-width: 768px) {
    .sites {
        max-width: 600px;
        margin-left: auto;
        margin-right: auto;
        margin-top: 30px;
    }

    .sites-wrapper {
        margin-right: -25px;
        justify-content: flex-start;
    }

    .site {
        width: 100px;
        margin-right: 25px;
    }

    input {
        width: 475px;
        height: 40px;
        font-size: 20px;
        opacity: 0.6;
    }

    button {
        font-size: 15px;
        opacity: 0.6;
    }

    .input-form {
        margin-top: 30vh;
        margin-left: auto;
        margin-right: auto;
        max-width: 600px;
    }

    .delete-button {
        opacity: 0;
        transition: 0.3s all ease;
    }

    .deleted{
        transition: 0.7s all ease;
        opacity: 0;
    }

    .site:hover .delete-button {
        opacity: 1;
        transition-delay: 0.1s;
    }

    .footer {
        font-size: 12px;
        margin: 0 8px 0px auto;
    }

    .underline {
        width: 480px;
    }

    .underline>span {
        font-size: 20px;
        left: 490px;
    }
}

