    opacity: 1;
}

input:focus+.underline,
input:valid+.underline {
    transform: translateX(0%);
}

input:focus+.underline>span,
input:valid+.underline>span {
    opacity: 0;
}

.underline>span {
    font-family: var(--maint-text-font);
    color: var(--main-text-color);
    font-size: 15px;
    display: unset;
    position: absolute;
    left: 103%;
    bottom: 5px;
    opacity: 0.7;
    transition: 0.5s all ease;
}


